import std.zlib;
import std.stdio;
import std.conv: to;
import std.algorithm: map;

UnCompress decmp = new UnCompress;
foreach (chunk; stdin.byChunk(4096).map!(x => decmp.uncompress(x)))
{
    chunk.to!string.write;
}

