version (Windows)
{
    assert (buildPath("foo", "bar", "baz") == `foo\bar\baz`);
    assert (buildPath(`c:\foo`, `bar\baz`) == `c:\foo\bar\baz`);
    assert (buildPath("foo", `d:\bar`)     == `d:\bar`);
    assert (buildPath("foo", `\bar`)       == `\bar`);
    assert (buildPath(`c:\foo`, `\bar`)    == `c:\bar`);
