    assert (asNormalizedPath("/foo/bar/baz/").array == "/foo/bar/baz");
    assert (asNormalizedPath("/foo/./bar/../../baz").array == "/baz");
}

version (Windows)
{
    assert (asNormalizedPath(`c:\foo\.\bar/..\\baz\`).array == `c:\foo\baz`);
