auto vandermondeMatrix(Slice!(1, double*) x)
{
    auto ret = new double[x.length ^^ 2]
        .sliced(x.length, x.length);
    foreach (i; 0 .. x.length)
    foreach (j; 0 .. x.length)
        ret[i, j] = x[i] ^^ j;
    return ret;
}

