import std.algorithm : filterBidirectional, equal;

auto a = [1, 2, 3];
a.popFrontExactly(1);
assert(a == [2, 3]);
a.popBackExactly(1);
assert(a == [2]);

string s = "æ¥æ¬èª";
s.popFrontExactly(1);
assert(s == "æ¬èª");
s.popBackExactly(1);
assert(s == "æ¬");

auto bd = filterBidirectional!"true"([1, 2, 3]);
bd.popFrontExactly(1);
assert(bd.equal([2, 3]));
bd.popBackExactly(1);
assert(bd.equal([2]));

